import{_ as e}from"./chunks/plugin-vue_export-helper.DlAUqK2U.js";import{c as a,o as i,ad as o}from"./chunks/mermaid.Cb40Nz1P.js";import"./chunks/cytoscape.C2CwDKBM.js";import"./chunks/dayjs.C32PoDnw.js";const f=JSON.parse('{"title":"Migrating to an AI-First Architecture","description":"","frontmatter":{},"headers":[],"relativePath":"use-cases/migrate-to-ai.md","filePath":"use-cases/migrate-to-ai.md"}'),r={name:"use-cases/migrate-to-ai.md"};function s(n,t,l,h,u,d){return i(),a("div",null,[...t[0]||(t[0]=[o('<h1 id="migrating-to-an-ai-first-architecture" tabindex="-1">Migrating to an AI-First Architecture <a class="header-anchor" href="#migrating-to-an-ai-first-architecture" aria-label="Permalink to &quot;Migrating to an AI-First Architecture&quot;">​</a></h1><p><strong>Problem</strong>: You have a monolithic app. Management wants &quot;AI everywhere&quot;. <strong>Risk</strong>: AI is non-deterministic. It might break core workflows.</p><h2 id="the-pattern-the-ai-sidecar" tabindex="-1">The Pattern: The AI Sidecar <a class="header-anchor" href="#the-pattern-the-ai-sidecar" aria-label="Permalink to &quot;The Pattern: The AI Sidecar&quot;">​</a></h2><p>Don&#39;t replace logic. <strong>Augment</strong> it.</p><h3 id="phase-1-the-assistant-low-risk" tabindex="-1">Phase 1: The &quot;Assistant&quot; (Low Risk) <a class="header-anchor" href="#phase-1-the-assistant-low-risk" aria-label="Permalink to &quot;Phase 1: The &quot;Assistant&quot; (Low Risk)&quot;">​</a></h3><p>Add a Floating Action Button (FAB) that opens a Chatbot.</p><ul><li><strong>Access</strong>: Read-only access to documentation.</li><li><strong>Impact</strong>: If it fails, the app still works.</li></ul><h3 id="phase-2-the-copilot-medium-risk" tabindex="-1">Phase 2: The &quot;Copilot&quot; (Medium Risk) <a class="header-anchor" href="#phase-2-the-copilot-medium-risk" aria-label="Permalink to &quot;Phase 2: The &quot;Copilot&quot; (Medium Risk)&quot;">​</a></h3><p>Add AI inside the form fields.</p><ul><li><strong>Feature</strong>: &quot;Auto-fill this form&quot; button.</li><li><strong>Impact</strong>: User must review and click &quot;Save&quot;. Human is still in the loop.</li></ul><h3 id="phase-3-the-agent-high-risk" tabindex="-1">Phase 3: The &quot;Agent&quot; (High Risk) <a class="header-anchor" href="#phase-3-the-agent-high-risk" aria-label="Permalink to &quot;Phase 3: The &quot;Agent&quot; (High Risk)&quot;">​</a></h3><p>AI takes actions in the background.</p><ul><li><strong>Feature</strong>: &quot;Auto-approve refund requests &lt; $50&quot;.</li><li><strong>Safety</strong>: Require a &quot;Confidence Score&quot; &gt; 99%. If 98%, route to human.</li></ul><h2 id="risk-mitigation" tabindex="-1">Risk Mitigation <a class="header-anchor" href="#risk-mitigation" aria-label="Permalink to &quot;Risk Mitigation&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Risk</th><th style="text-align:left;">Mitigation</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Hallucination</strong></td><td style="text-align:left;">Use RAG + Citations. Never let AI guess.</td></tr><tr><td style="text-align:left;"><strong>Cost Explosion</strong></td><td style="text-align:left;">Implement strict Rate Limits per user.</td></tr><tr><td style="text-align:left;"><strong>Data Leak</strong></td><td style="text-align:left;">PII Redaction Middleware.</td></tr><tr><td style="text-align:left;"><strong>UX Confusion</strong></td><td style="text-align:left;">Use different UI styles (e.g., Purple Sparkles) for AI content.</td></tr></tbody></table>',15)])])}const q=e(r,[["render",s]]);export{f as __pageData,q as default};
