import{_ as a}from"./chunks/plugin-vue_export-helper.DlAUqK2U.js";import{c as e,o as r,ad as l}from"./chunks/mermaid.Cb40Nz1P.js";import"./chunks/cytoscape.C2CwDKBM.js";import"./chunks/dayjs.C32PoDnw.js";const m=JSON.parse('{"title":"迁移到 AI 优先架构","description":"","frontmatter":{},"headers":[],"relativePath":"zh/use-cases/migrate-to-ai.md","filePath":"zh/use-cases/migrate-to-ai.md"}'),o={name:"zh/use-cases/migrate-to-ai.md"};function i(s,t,n,d,g,h){return r(),e("div",null,[...t[0]||(t[0]=[l('<h1 id="迁移到-ai-优先架构" tabindex="-1">迁移到 AI 优先架构 <a class="header-anchor" href="#迁移到-ai-优先架构" aria-label="Permalink to &quot;迁移到 AI 优先架构&quot;">​</a></h1><p><strong>问题</strong>: 你有一个单体应用。管理层想要“处处有 AI”。 <strong>风险</strong>: AI 是非确定性的。它可能会破坏核心工作流。</p><h2 id="模式-ai-sidecar-边车" tabindex="-1">模式：AI Sidecar (边车) <a class="header-anchor" href="#模式-ai-sidecar-边车" aria-label="Permalink to &quot;模式：AI Sidecar (边车)&quot;">​</a></h2><p>不要替换逻辑。<strong>增强</strong>它。</p><h3 id="阶段-1-助手-低风险" tabindex="-1">阶段 1: “助手” (低风险) <a class="header-anchor" href="#阶段-1-助手-低风险" aria-label="Permalink to &quot;阶段 1: “助手” (低风险)&quot;">​</a></h3><p>添加一个悬浮操作按钮 (FAB)，打开聊天机器人。</p><ul><li><strong>访问权限</strong>: 对文档的只读访问权限。</li><li><strong>影响</strong>: 如果它失败了，应用仍然可以工作。</li></ul><h3 id="阶段-2-副驾驶-中等风险" tabindex="-1">阶段 2: “副驾驶” (中等风险) <a class="header-anchor" href="#阶段-2-副驾驶-中等风险" aria-label="Permalink to &quot;阶段 2: “副驾驶” (中等风险)&quot;">​</a></h3><p>在表单字段内添加 AI。</p><ul><li><strong>功能</strong>: “自动填充此表单”按钮。</li><li><strong>影响</strong>: 用户必须审查并点击“保存”。人仍在循环中。</li></ul><h3 id="阶段-3-智能体-高风险" tabindex="-1">阶段 3: “智能体” (高风险) <a class="header-anchor" href="#阶段-3-智能体-高风险" aria-label="Permalink to &quot;阶段 3: “智能体” (高风险)&quot;">​</a></h3><p>AI 在后台采取行动。</p><ul><li><strong>功能</strong>: “自动批准 &lt; $50 的退款请求”。</li><li><strong>安全性</strong>: 要求“置信度分数” &gt; 99%。如果是 98%，则路由给人工。</li></ul><h2 id="风险缓解" tabindex="-1">风险缓解 <a class="header-anchor" href="#风险缓解" aria-label="Permalink to &quot;风险缓解&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">风险</th><th style="text-align:left;">缓解措施</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>幻觉</strong></td><td style="text-align:left;">使用 RAG + 引用。永远不要让 AI 猜测。</td></tr><tr><td style="text-align:left;"><strong>成本爆炸</strong></td><td style="text-align:left;">对每个用户实施严格的速率限制。</td></tr><tr><td style="text-align:left;"><strong>数据泄露</strong></td><td style="text-align:left;">PII 编校中间件。</td></tr><tr><td style="text-align:left;"><strong>UX 困惑</strong></td><td style="text-align:left;">对 AI 内容使用不同的 UI 风格（例如，紫色火花）。</td></tr></tbody></table>',15)])])}const _=a(o,[["render",i]]);export{m as __pageData,_ as default};
